import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import Layout from "../components/Layout/Layout";
import '../styles/Login.css';

function ReservationModel() {
    const [values, setValues] = useState({
        date: '',
        time: '',
        numberOfTables: ''
    });
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    const handleChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (validateForm()) {
            axios.post('http://localhost:3001/reservations', values)
                .then(res => {
                    console.log(res);
                    navigate('/admin');
                })
                .catch(err => console.log(err));
        }
    };

    const validateForm = () => {
        let errors = {};
        let isValid = true;

        if (!values.date) {
            errors.date = "Date is required";
            isValid = false;
        }

        if (!values.time) {
            errors.time = "Time is required";
            isValid = false;
        }

        if (!values.numberOfTables) {
            errors.numberOfTables = "Number of tables is required";
            isValid = false;
        }

        setErrors(errors);
        return isValid;
    };

    return (
        <Layout>
            <h1>Restaurant Reservation System</h1>
            <div className="d-flex w-100 vh-100 justify-content-center backgroundcolorset">
                <div className="w-50 border border-black shadow bg-info px-5 pt-3 pb-5 rounded border-set">
                    <h3>Make a Reservation</h3><br />
                    <form onSubmit={handleSubmit}>
                        <div className="mb-2">
                            <label htmlFor="date">Date:</label>
                            <input
                                required
                                type="date"
                                name="date"
                                className="form-control"
                                placeholder="Enter Date"
                                onChange={handleChange}
                            />
                            {errors.date && <span className="text-danger">{errors.date}</span>}
                        </div><br />
                        <div className="mb-2">
                            <label htmlFor="time">Time:</label>
                            <input
                                required
                                type="time"
                                name="time"
                                className="form-control"
                                placeholder="Enter Time"
                                onChange={handleChange}
                            />
                            {errors.time && <span className="text-danger">{errors.time}</span>}
                        </div><br />
                        <div className="mb-2">
                            <label htmlFor="numberOfTables">Number of Tables:</label>
                            <input
                                required
                                type="number"
                                name="numberOfTables"
                                className="form-control"
                                placeholder="Enter Number of Tables"
                                onChange={handleChange}
                            />
                            {errors.numberOfTables && <span className="text-danger">{errors.numberOfTables}</span>}
                        </div><br />
                        <button className="btn btn-success m-1" type="submit">Save</button>
                        <Link to="/" className="btn btn-primary m-1">Back</Link>
                    </form>
                </div>
            </div>
        </Layout>
    );
}

export default ReservationModel;
