import React, { useState } from "react";
import { Button, Card, CardBody, Container, Form } from "react-bootstrap";
import axios from "axios";
import { NavLink, useNavigate } from 'react-router-dom'

const initialValues = {
    restaurant_Name: "",
    email_Id: "",
    contactNumber: "",
    location: "",
    type: "",
    status: "",
    cuisine: "",
    totalTables: "",
    restaurantImage: null,
};

function RestaurantMenu() {
  const [values, setValues] = useState(initialValues);
  const navigate = useNavigate();

  const handleInput = (e) => {
    setValues({
      ...values,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    setValues({
      ...values,
      restaurantImage: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    // Append each value to the formData
    Object.entries(values).forEach(([key, value]) => {
      formData.append(key, value);
    });

    try {
      const response = await axios.post(
        "https://localhost:7092/api/Restaurant_Details",
        formData, // Send formData instead of values directly
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      // Handle success
      window.alert("Posted successfully");
      console.log("Posted successfully:", response.data);
      navigate('/restaurantlist');
    } catch (error) {
      // Handle error
      console.error("Post failure:", error);
    }
  };

  return (
    <Container>
      <Card>
        <CardBody>
          <Form onSubmit={handleSubmit}>
           
          </Form>
        </CardBody>
      </Card>
    </Container>
  );
}

export default RestaurantMenu;
