import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Cookies from 'js-cookie';
import Layout from "../components/Layout/Layout";

function BookingView() {
    const [reservations, setReservations] = useState([]);
    const email=Cookies.get('Email'); // Get the customer's email from cookies

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`https://localhost:7092/api/Reservation/ByEmail/${email}`);
                setReservations(response.data);
                // Save to cookies
                Cookies.set('reservations', JSON.stringify(response.data));
            } catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, [email]); // Add email as a dependency

    const handleDelete = async (id) => {
        const confirmDelete = window.confirm("Would you like to cancel the reservation?");
        if (confirmDelete) {
            try {
                await axios.delete(`https://localhost:7092/api/Reservation/${id}`);
                const updatedReservations = reservations.filter(reservation => reservation.reqId !== id);
                setReservations(updatedReservations);
                // Update cookies
                Cookies.set('reservations', JSON.stringify(updatedReservations));
            } catch (err) {
                console.log(err);
            }
        }
    };

    return (
        <Layout>
            <h1>Booking Cart</h1> <Link to="/home" className="btn btn-primary m-1">Back</Link>
            <div className="cart-container">
                {reservations.map((reservation, index) => (
                    <div key={index} className="cart-item">
                        <h3>Reservation ID: {reservation.reqId}</h3>
                        <p>Customer Id: {reservation.customer_Id}</p>
                        <button onClick={() => handleDelete(reservation.reqId)} className="btn btn-danger m-1">Cancel</button>
                    </div>
                ))}
            </div>
        </Layout>
    );
}

export default BookingView;
