import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateRestaurant = ({ id }) => {
    const [restaurantDetails, setRestaurantDetails] = useState({
        Restaurant_Id: '',
        Restaurant_Name: '',
        Email_Id: '',
        ContactNumber: '',
        Password: '',
        Location: '',
        Type: '',
        Cuisine: '',
        TotalTables: '',
        Status: '',
        Personal_Email: ''
    });

    useEffect(() => {
        // Fetch the current details
        axios.get(`/api/restaurant_details/${id}`)
            .then(response => {
                setRestaurantDetails(response.data);
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    }, [id]);

    const handleInputChange = (event) => {
        setRestaurantDetails({
            ...restaurantDetails,
            [event.target.name]: event.target.value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Update the restaurant details
        axios.put(`/api/restaurant_details/${id}`, restaurantDetails)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Add other fields as necessary */}
            <label>
                Restaurant Name:
                <input type="text" name="Restaurant_Name" value={restaurantDetails.Restaurant_Name} onChange={handleInputChange} />
            </label>
            <label>
                Email:
                <input type="email" name="Email_Id" value={restaurantDetails.Email_Id} onChange={handleInputChange} />
            </label>
            <label>
                Contact Number:
                <input type="text" name="ContactNumber" value={restaurantDetails.ContactNumber} onChange={handleInputChange} />
            </label>
            <label>
                Location:
                <input type="text" name="Location" value={restaurantDetails.Location} onChange={handleInputChange} />
            </label>
            <label>
                Type:
                <input type="text" name="Type" value={restaurantDetails.Type} onChange={handleInputChange} />
            </label>
            <label>
                Cuisine:
                <input type="text" name="Cuisine" value={restaurantDetails.Cuisine} onChange={handleInputChange} />
            </label>
            <label>
                Total Tables:
                <input type="number" name="TotalTables" value={restaurantDetails.TotalTables} onChange={handleInputChange} />
            </label>
            <label>
                Status:
                <input type="text" name="Status" value={restaurantDetails.Status} onChange={handleInputChange} />
            </label>
            <label>
                Personal Email:
                <input type="email" name="Personal_Email" value={restaurantDetails.Personal_Email} onChange={handleInputChange} />
            </label>
            <button type="submit">Update</button>
        </form>
    );
};

export default UpdateRestaurant;
