import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateRestaurantForm = ({ restaurantId }) => {
  const [restaurant, setRestaurant] = useState({
    restaurant_Name: '',
    email_Id: '',
    contactNumber: '',
    password: '',
    location: '',
    type: '',
    cuisine: '',
    totalTables: 0,
    status: '',
    personal_Email: ''
  });

  useEffect(() => {
    axios.get(`https://localhost:7092/api/Restaurant_Details/${restaurantId}`)
      .then(res => setRestaurant(res.data))
      .catch(err => console.log(err));
  }, [restaurantId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRestaurant({ ...restaurant, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`https://localhost:7092/api/Restaurant_Details/${restaurantId}`, restaurant)
      .then(res => console.log(res.data))
      .catch(err => console.log(err));
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Restaurant Name:
        <input type="text" name="restaurant_Name" value={restaurant.restaurant_Name} onChange={handleChange} />
      </label>
      <label>
        Email:
        <input type="email" name="email_Id" value={restaurant.email_Id} onChange={handleChange} />
      </label>
      <label>
        Contact Number:
        <input type="text" name="contactNumber" value={restaurant.contactNumber} onChange={handleChange} />
      </label>
      <label>
        Password:
        <input type="password" name="password" value={restaurant.password} onChange={handleChange} />
      </label>
      <label>
        Location:
        <input type="text" name="location" value={restaurant.location} onChange={handleChange} />
      </label>
      <label>
        Type:
        <input type="text" name="type" value={restaurant.type} onChange={handleChange} />
      </label>
      <label>
        Cuisine:
        <input type="text" name="cuisine" value={restaurant.cuisine} onChange={handleChange} />
      </label>
      <label>
        Total Tables:
        <input type="number" name="totalTables" value={restaurant.totalTables} onChange={handleChange} />
      </label>
      <label>
        Status:
        <input type="text" name="status" value={restaurant.status} onChange={handleChange} />
      </label>
      <label>
        Personal Email:
        <input type="email" name="personal_Email" value={restaurant.personal_Email} onChange={handleChange} />
      </label>
      <button type="submit">Update Restaurant</button>
    </form>
  );
};

export default UpdateRestaurantForm;
