import React, { useState } from "react";
import { Button, Card, CardBody, Container, Form } from "react-bootstrap";
import axios from "axios";
import { NavLink,useNavigate } from 'react-router-dom'
// import 'bootstrap/dist/css/bootstrap.min.css';

const initialValues = {
    restaurant_Name:"",
    email_Id:"",
    contactNumber:"",
    location:"",
    type:"",
    cuisine:"",
    totalTables:"",
    restaurantImage:"",
};

function RestaurantMenu() {
  const [values, setValues] = useState(initialValues);
  const navigate = useNavigate();

  const handleInput = (e) => {
    setValues({
      ...values,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    setValues({
      ...values,
      menuImage: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(values).forEach(([key, value]) => {
      formData.append(key, value);
    });

    try {
      const response = await axios.post(
        "https://localhost:44304/api/Restaurants",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      window.alert("Posted successfully");
      console.log("Posted successfully:", response.data);
      navigate('/restaurantlist');
    } catch (error) {
      console.error("Post failure:", error);
    }
  };

  return (
    <Container>
      <Card>
        <CardBody>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Restaurant Name :</Form.Label>
              <Form.Control
                type="text"
                name="Restaurant_Name"
                value={values.Restaurant_Name}
                onChange={handleInput}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Email Id:</Form.Label>
              <Form.Control
                type="text"
                name="Email_Id"
                value={values. Email_Id}
                onChange={handleInput}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>ContactNumber:</Form.Label>
              <Form.Control
                type="text"
                name="ContactNumber"
                value={values.ContactNumber}
                onChange={handleInput}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label> Location:</Form.Label>
              <Form.Control
                type="text"
                name="Location"
                value={values. Location}
                onChange={handleInput}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Type:</Form.Label>
              <Form.Control
                type="text"
                name="Type"
                value={values.Type}
                onChange={handleInput}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Cuisine:</Form.Label>
              <Form.Control
                type="text"
                name="Cuisine"
                value={values.Cuisine}
                onChange={handleInput}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>TotalTables:</Form.Label>
              <Form.Control
                type="text"
                name="TotalTables"
                value={values.TotalTables}
                onChange={handleInput}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Status:</Form.Label>
              <Form.Control
                type="text"
                name="Status"
                value={values.Status}
                onChange={handleInput}
              />
            </Form.Group>
         

          

            <Form.Group className="mb-3">
              <Form.Label>Restaurant Image:</Form.Label>
              <Form.Control
                type="file"
                name="restaurantImage"
                accept="image/*"
                onChange={handleImageChange}
              />
            </Form.Group>

            <Button type="submit">Submit</Button>
          </Form>
        </CardBody>
      </Card>
    </Container>
  );
}

export default RestaurantMenu;
