import React, { useState, useEffect } from 'react';
import { Button, Card, CardBody, Container, Form } from "react-bootstrap";
import axios from 'axios';

const UpdateRestaurantForm = ({ restaurantId }) => {
  const [restaurant, setRestaurant] = useState({
    restaurant_Name: '',
    email_Id: '',
    contactNumber: '',
    password: '',
    location: '',
    type: '',
    cuisine: '',
    totalTables: 0,
    status: 'waiting for approval',
    personal_Email: '',
    restaurantImage: null,
    uniqueFileName: '',
  });

  // Fetch existing restaurant data (optional)
  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const response = await axios.get(`https://localhost:7092/api/Restaurant_Details/${restaurantId}`);
        setRestaurant(response.data);
        console.log(response);
      } catch (error) {
        console.error('Error fetching restaurant:', error);
      }
    };

    fetchRestaurant();
  }, [restaurantId, restaurant]);

  const handleInput = (e) => {
    const { name, value } = e.target;
    setRestaurant({ ...restaurant, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setRestaurant({ ...restaurant, restaurantImage: file });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('Restaurant_Name', restaurant.restaurant_Name);
    formData.append('Email_Id', restaurant.email_Id);
    formData.append('ContactNumber', restaurant.contactNumber);
    formData.append('Password', restaurant.password);
    formData.append('Location', restaurant.location);
    formData.append('Type', restaurant.type);
    formData.append('Cuisine', restaurant.cuisine);
    formData.append('TotalTables', restaurant.totalTables);
    formData.append('Status', restaurant.status);
    formData.append('Personal_Email', restaurant.personal_Email);
    formData.append('RestaurantImage', restaurant.restaurantImage);

    try {
      await axios.put(`https://localhost:7092/api/Restaurant_Details/${restaurantId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log(formData)
      console.log('Restaurant updated successfully');
    } catch (error) {
      console.error('Error updating restaurant:', error);
    }
  };

  return (
    <Container>
    <Card>
      <CardBody>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Restaurant Name :</Form.Label>
            <Form.Control
              type="text"
              name="restaurant_Name"
              value={restaurant.restaurant_Name}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Email Id:</Form.Label>
            <Form.Control
              type="text"
              name="email_Id"
              value={restaurant.email_Id}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>ContactNumber:</Form.Label>
            <Form.Control
              type="text"
              name="contactNumber"
              value={restaurant.contactNumber}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label> Location:</Form.Label>
            <Form.Control
              type="text"
              name="location"
              value={restaurant.location}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Type:</Form.Label>
            <Form.Control
              type="text"
              name="type"
              value={restaurant.type}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Cuisine:</Form.Label>
            <Form.Control
              type="text"
              name="cuisine"
              value={restaurant.cuisine}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>TotalTables:</Form.Label>
            <Form.Control
              type="number"
              name="totalTables"
              value={restaurant.totalTables}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Status:</Form.Label>
            <Form.Control
              type="text"
              name="status"
              value={restaurant.status}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Personal_Email:</Form.Label>
            <Form.Control
              type="text"
              name="personal_Email"
              value={restaurant.personal_Email}
              onChange={handleInput}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Restaurant Image:</Form.Label>
            <Form.Control
              type="file"
              name="restaurantImage"
              accept="image/*"
              onChange={handleImageChange}
            />
          </Form.Group>

          <Button type="submit">Update</Button>
        </Form>
      </CardBody>
    </Card>
  </Container>
  );
};

export default UpdateRestaurantForm;
