import React, { useState, useEffect } from "react";
import axios from "axios";

function Menu() {
    const [imageSrc, setImageSrc] = useState([]);
 
    const [menus, setServices] = useState([]);
    const [selectedmenu, setSelectedService] = useState(null);
    useEffect(() => {
        fetchServices();
    }, []);
 
    const fetchServices = async () => {
        try {
            const response = await axios.get('http://localhost:5151/api/Backend/GetAllCart'); // Assuming the API endpoint is correct
            const productsWithImages = await Promise.all(response.data.map(async (product) => {
                try {
                    const imageResponse = await axios.get(`http://localhost:5151/api/Backend/GetImage/${product.id}/Image`, {
                        responseType: 'arraybuffer',
                    });
                    const imageUrl = URL.createObjectURL(new Blob([imageResponse.data], { type: 'image/jpeg' }));
                    return { ...product, imageUrl };
                } catch (error) {
                    console.error('Error fetching image for product:', product.id, error);
                    return product;
                }
            }));
            setServices(productsWithImages);
            console.log("set",menus)
        }
           
           
         catch (error) {
            console.error('Error fetching products:', error);
        }
    };
    const handleMenuDetails = (menus) => {
        setSelectedService(menus);
    }

    return (
        <div className="menu-list">
            <div className="menus-container panel panel-default text-center">
                {menus.map((menu, index) => (
                    <div key={menu.MenuID} className="menu-item">
                        <div className="panel-header">
                            <h2>{menu.Name}</h2>
                        </div>
                        <div className="panel-body">
                            <img src={menu.MenuImage} alt={menu.Name} />
                        </div>
                        <div className="panel-footer">
                          
                            <p>Price: {menu.Price}</p>
                            <button className="btn btn-lg" type="button" onClick={() => handleMenuDetails(menu)}>View Details</button>
                        </div>
                    </div>
                ))}
            </div>
            <div className="menu-detail bg-warning">
                {selectedMenu && (
                    <div>
                        <h2>{selectedMenu.Name}</h2>
                        {/* <p>Description: {selectedMenu.Description}</p> */}
                        <p>Price: {selectedMenu.Price}</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Menu;
