import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Login.css';

import Bannerq from "../images/Login-background.jpg";

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://localhost:7092/api/Login/login', { email, password });
      console.log(response.data);
      window.alert('User Registered successfuly'); 
      // Handle success, e.g., redirect to dashboard
    } catch (error) {
      if(error.response)
      setError('Username or password is incorrect');
    }
  };

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-md-5 mx-auto">
            <div id="first">
              <div className="myform form">
                <div className="logo mb-3">
                  <div className="col-md-12 text-center">
                    <h1>Login</h1>
                  </div>
                </div>
                <form onSubmit={handleSubmit} name="login">
                  <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Email address</label><br /><br />
                    <input
                      type="email"
                      name="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className="form-control"
                      id="email"
                      aria-describedby="emailHelp"
                      placeholder="Enter email"
                      required
                    />
                  </div><br />
                  <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Password</label><br /><br />
                    <input
                      type="password"
                      name="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      className="form-control"
                      id="password"
                      aria-describedby="emailHelp"
                      placeholder="Enter Password"
                      required
                    />
                  </div>

                  <div className="col-12">
                    <button className="btn btn-primary button-set" type="submit">Login</button>
                  </div>
                  <div className="col-md-12 ">
                    <div className="login-or">
                      <hr className="hr-or" />
                      <span className="span-or">or</span>
                    </div>
                  </div>

                  <div className="form-group">
                    <p className="text-center">Don't have account? <a href="#" id="signup">Sign up here</a></p>
                  </div>
                </form>
                {error && <div className="alert alert-danger" role="alert">{error}</div>}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Login;
