import React, { useState, useEffect } from "react";
import axios from "axios";

function DisplayService() {
    const [services, setServices] = useState([]);
    const [selectedService, setSelectedService] = useState(null);

    useEffect(() => {
        fetchServices();
    }, []);

    const fetchServices = async () => {
        try {
            const response = await axios.get('http://localhost:5151/api/Menu'); // Assuming the API endpoint is correct
            const menusWithImages = await Promise.all(response.data.map(async (menu) => {
                try {
                    const imageResponse = await axios.get(`http://localhost:5151/api/Menu/GetImage/${menu.MenuID}`, {
                        responseType: 'arraybuffer',
                    });
                    const imageUrl = URL.createObjectURL(new Blob([imageResponse.data], { type: 'image/jpeg' }));
                    return { ...menu, imageUrl };
                } catch (error) {
                    console.error('Error fetching image for menu:', menu.MenuID, error);
                    return menu;
                }
            }));
            setServices(menusWithImages);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    };

    const handleServiceDetails = (service) => {
        setSelectedService(service);
    };

    return (
        <div className="service-list">
            <div className="services-container panel panel-default text-center">
                {services.map((service, index) => (
                    <div key={service.MenuID} className="service-item">
                        <div className="panel-header">
                            <h2>{service.Name}</h2>
                        </div>
                        <div className="panel-body">
                            <img src={service.imageUrl} alt={service.Name} />
                        </div>
                        <div className="panel-footer">
                            <p>Description: {service.Description}</p>
                            <p>Price: {service.Price}</p>
                            <button className="btn btn-lg" type="button" onClick={() => handleServiceDetails(service)}>View Details</button>
                        </div>
                    </div>
                ))}
            </div>
            <div className='service-detail bg-warning'>
                {selectedService && (
                    <div>
                        <h2>{selectedService.Name}</h2>
                        <p>Description: {selectedService.Description}</p>
                        <p>Price: {selectedService.Price}</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default DisplayService;
