import React, { useState } from 'react';
import axios from 'axios';

function EmailForm() {
  const [restaurantDetails, setRestaurantDetails] = useState({
    restaurant_Id: '',
    restaurant_Name: '',
    Password: '',
    Personal_Email: '',
    // Add other fields as necessary
  });

  const handleInputChange = (e) => {
    setRestaurantDetails({ ...restaurantDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(restaurantDetails).forEach(([key, value]) => {
      formData.append(key, value);
    });

    try {
      const response = await axios.post('https://localhost:7092/api/Email/SendingEmail', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log(response.data);
      alert('Email sent successfully!');
    } catch (error) {
      console.error('Error sending email:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="Restaurant_Id"
        placeholder="Restaurant ID"
        value={restaurantDetails.Restaurant_Id}
        onChange={handleInputChange}
        required
      />
      <input
        type="text"
        name="Restaurant_Name"
        placeholder="Restaurant Name"
        value={restaurantDetails.Restaurant_Name}
        onChange={handleInputChange}
        required
      />
      <input
        type="password"
        name="Password"
        placeholder="Password"
        value={restaurantDetails.Password}
        onChange={handleInputChange}
        required
      />
      <input
        type="email"
        name="Personal_Email"
        placeholder="Personal Email"
        value={restaurantDetails.Personal_Email}
        onChange={handleInputChange}
        required
      />
      {/* Add other input fields as necessary */}
      <button type="submit">Send Email</button>
    </form>
  );
}

export default EmailForm;
