import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

function AdminView() {
    const [restaurants, setRestaurants] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get("https://localhost:7092/api/Restaurant_Details")
            .then(res => setRestaurants(res.data))
            .catch(err => console.log(err));
    }, []);

    const handleDelete = (id) => {
        const confirmDelete = window.confirm("Are you sure you want to delete?");
        if (confirmDelete) {
            axios.delete(`https://localhost:7092/api/Restaurant_Details/${id}`)
                .then(res => {
                    setRestaurants(restaurants.filter(r => r.Restaurant_Id !== id));
                    window.alert
                })
                .catch(err => console.log(err));
        }
    };

    return (
        <>
            <h1>List of Restaurants</h1>
            <div className="d-flex flex-column  align-items-center backgroundcolorset vh-100">
                <div className="w-100 rounded bg-info border shadow p-4">
                    <div className="d-flex justify-content-end">
                        <Link to="/admincreate" className="btn btn-sm btn-success">Add +</Link>
                    </div>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>Restaurant ID</th>
                                <th>Restaurant Name</th>
                                <th>Email</th>
                                <th>Contact Number</th>
                                <th>Location</th>
                                <th>Type</th>
                                <th>Cuisine</th>
                                <th>Total Tables</th>
                                <th>Status</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {restaurants.map((restaurant, index) => (
                                <tr key={index}>
                                    <td>{restaurant.Restaurant_Id}</td>
                                    <td>{restaurant.Restaurant_Name}</td>
                                    <td>{restaurant.Email_Id}</td>
                                    <td>{restaurant.ContactNumber}</td>
                                    <td>{restaurant.Location}</td>
                                    <td>{restaurant.Type}</td>
                                    <td>{restaurant.Cuisine}</td>
                                    <td>{restaurant.TotalTables}</td>
                                    <td>{restaurant.Status}</td>
                                    <td>
                                        <Link to={`/adminread/${restaurant.Restaurant_Id}`} className="btn btn-info m-1">View Details</Link>
                                        <Link to={`/adminupdate/${restaurant.Restaurant_Id}`} className="btn btn-primary m-1">Edit Details</Link>
                                        <button onClick={() => handleDelete(restaurant.Restaurant_Id)} className="btn btn-danger m-1">Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default AdminView;
