                // Save to cookies
                Cookies.set('restaurants', JSON.stringify(response.data));
            } catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, []);

    const handleDelete = async (id) => {
        const confirmDelete = window.confirm("Would you like to delete?");
        if (confirmDelete) {
            try {
                await axios.delete(`https://localhost:7092/api/Reservation/${id}`);
                const updatedRestaurants = restaurants.filter(restaurant => restaurant.reqId !== id);
                setRestaurants(updatedRestaurants);
                // Update cookies
                Cookies.set('restaurants', JSON.stringify(updatedRestaurants));
            } catch (err) {
                console.log(err);
            }
        }
    };

    return (
        <Layout>
            <h1>Booking Cart</h1> <Link to="/home" className="btn btn-primary m-1">Back</Link>
            <div className="cart-container">
                {restaurants.map((restaurant, index) => (
                    <div key={index} className="cart-item">
                        <h3>Reservation ID: {restaurant.reqId}</h3>
                        <p>Customer Id: {restaurant.customer_Id}</p>
                        <p>Date: {restaurant.date}</p>
                        <p>Time: {restaurant.time}</p>
                        <p>No Of Tables: {restaurant.noOfTables}</p>
                        <button onClick={() => handleDelete(restaurant.reqId)} className="btn btn-danger m-1">Remove from Cart</button>
                    </div>
                ))}
            </div>
        </Layout>
    );
}

export default BookingView;
