import React, { useState} from 'react';
import { NavLink ,useNavigate} from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css';
import "../styles/HomeStyles.css";

import Bannerq from "../images/Login-background.jpg";
import Layout from '../components/Layout/Layout';

function Login() {
   const navigate=useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  
   const regex = /\b[A-Za-z0-9._%+-]+@gmail\.com\b/;

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://localhost:7092/api/Login/login', { email, password });
      console.log(response.data);
      window.alert('User Registered successfuly'); 
      // navigate('/home');
      if(!response.email){
        error.Email="Email is required!";
    } else if (regex.test(response.email)){
        navigate("/centerdash");
    }else if (response.email==='admin@gmail.com'&& response.Password==='12345'){
      navigate("/adminhome");
    }else{
      navigate("/home")
    }
      // Handle success, e.g., redirect to dashboard
    } catch (error) {
      setError(error.response.data.message);
    }
  };


  return (
    <Layout>
    <div className="home" >
      <div className="container">
        <div className="row">
          <div className="col-md-5 mx-auto">
            <div id="first">
              <div className="myform form">
                <div className="logo mb-3">
                  <div className="col-md-12 text-center">
                    <h1>Login</h1>
                  </div>
                </div>
                <form onSubmit={handleSubmit} name="login">
                  <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Email address</label><br /><br />
                    <input
                      type="email"
                      name="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className="form-control"
                      id="email"
                      aria-describedby="emailHelp"
                      placeholder="Enter email"
                      required
                    />
                  </div><br />
                  <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Password</label><br /><br />
                    <input
                      type="password"
                      name="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      className="form-control"
                      id="password"
                      aria-describedby="emailHelp"
                      placeholder="Enter Password"
                      required
                    />
                  </div>

                  <div className="col-12">
                    <button className="btn btn-primary button-set" type="submit">Login</button>
                  </div>
                  <div className="col-md-12 ">
                    <div className="login-or">
                      <hr className="hr-or" />
                      <span className="span-or">or</span>
                    </div>
                  </div>

                  <div className="form-group">
                    <p className="text-center">Don't have account? <a href="/signup" id="signup">Sign up here</a></p>
                  </div>
                </form>
                {error && <div className="alert alert-danger" role="alert">{error}</div>}
              </div>
            </div>
          </div>
        </div>
      </div>
   </div>
    </Layout>
  );
};

export default Login;
