// Assuming you have a React component where you want to call the email service API
import React, { useState } from 'react';
import axios from 'axios';

const EmailForm = () => {
  const [restaurantDetails, setRestaurantDetails] = useState({
    Restaurant_Id: '',
    Restaurant_Name: '',
    Password: '',
    Personal_Email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRestaurantDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Call your API endpoint to send the email
      await axios.post('https://localhost:7092/api/Email/SendingEmail', restaurantDetails);
      console.log('Email sent successfully!');
    } catch (error) {
      console.error('Error sending email:', error);
    }
  };

  return (
    <div>
      <h2>Send Email</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="Restaurant_Id"
          placeholder="Restaurant ID"
          value={restaurantDetails.Restaurant_Id}
          onChange={handleChange}
        />
        <input
          type="text"
          name="Restaurant_Name"
          placeholder="Restaurant Name"
          value={restaurantDetails.Restaurant_Name}
          onChange={handleChange}
        />
        <input
          type="password"
          name="Password"
          placeholder="Password"
          value={restaurantDetails.Password}
          onChange={handleChange}
        />
        <input
          type="email"
          name="Personal_Email"
          placeholder="Personal Email"
          value={restaurantDetails.Personal_Email}
          onChange={handleChange}
        />
        <button type="submit">Send Email</button>
      </form>
    </div>
  );
};

export default EmailForm;
