import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateRestaurantForm = ({ restaurantId }) => {
  const [restaurant, setRestaurant] = useState({
    restaurant_Name: '',
    email_Id: '',
    contactNumber: '',
    password: '',
    location: '',
    type: '',
    cuisine: '',
    totalTables: 0,
    status: 'waiting for approval',
    personal_Email: '',
    restaurantImage: null,
    uniqueFileName: '',
  });

  // Fetch existing restaurant data (optional)
  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const response = await axios.get(`/api/Restaurant/${restaurantId}`);
        setRestaurant(response.data);
      } catch (error) {
        console.error('Error fetching restaurant:', error);
      }
    };

    fetchRestaurant();
  }, [restaurantId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setRestaurant({ ...restaurant, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setRestaurant({ ...restaurant, RestaurantImage: file });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('Restaurant_Name', restaurant.restaurant_Name);
    formData.append('Email_Id', restaurant.email_Id);
    formData.append('ContactNumber', restaurant.contactNumber);
    formData.append('Password', restaurant.password);
    formData.append('Location', restaurant.location);
    formData.append('Type', restaurant.type);
    formData.append('Cuisine', restaurant.cuisine);
    formData.append('TotalTables', restaurant.totalTables);
    formData.append('Status', restaurant.status);
    formData.append('Personal_Email', restaurant.personal_Email);
    formData.append('RestaurantImage', restaurant.restaurantImage);

    try {
      await axios.put(`/api/Restaurant/${restaurantId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Restaurant updated successfully');
    } catch (error) {
      console.error('Error updating restaurant:', error);
    }
  };

  return (
    <>
    <div className="d-flex w-100 vh-100 justify-content-center backgroundcolorset">
            <div className="w-50 border  border-black shadow bg-white px-5 pt-3 pb-5 rounded border-set ">
                <h3>Add Restaurant</h3><br/>
                <form onSubmit={handleSubmit}>
                    <div className="mb-2">
                        <label htmlFor="name">Restaurant Name:</label>
                        <input
                            type="text"
                            name="name"
                            className="form-control"
                            placeholder="Enter Restaurant Name"
                            value={values.Restaurant_Name}
                            onChange={e => setValues({ ...values, Restaurant_Name: e.target.value })}
                            required
                        />
                        {errors.Restaurant_Name && <div className="text-danger">{errors.Restaurant_Name}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="email">Email:</label>
                        <input
                            type="email"
                            name="email"
                            className="form-control"
                            placeholder="Enter Email"
                            value={values.Email_Id}
                            onChange={e => setValues({ ...values, Email_Id: e.target.value })}
                            required
                        />
                        {errors.Email_Id && <div className="text-danger">{errors.Email_Id}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="contact">Contact Number:</label>
                        <input
                            type="text"
                            name="contact"
                            className="form-control"
                            placeholder="Enter Contact Number"
                            value={values.ContactNumber}
                            onChange={e => setValues({ ...values, ContactNumber: e.target.value })}
                            required
                        />
                        {errors.ContactNumber && <div className="text-danger">{errors.ContactNumber}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="password">Password:</label>
                        <input
                            type="password"
                            name="password"
                            className="form-control"
                            placeholder="Enter Password"
                            value={values.Password}
                            onChange={e => setValues({ ...values, Password: e.target.value })}
                            required
                        />
                        {errors.Password && <div className="text-danger">{errors.Password}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="location">Location:</label>
                        <input
                            type="text"
                            name="location"
                            className="form-control"
                            placeholder="Enter Location"
                            value={values.Location}
                            onChange={e => setValues({ ...values, Location: e.target.value })}
                            required
                        />
                        {errors.Location && <div className="text-danger">{errors.Location}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="type">Type:</label>
                        <input
                            type="text"
                            name="type"
                            className="form-control"
                            placeholder="Enter Type"
                            value={values.Type}
                            onChange={e => setValues({ ...values, Type: e.target.value })}
                            required
                        />
                        {errors.Type && <div className="text-danger">{errors.Type}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="cuisine">Cuisine:</label>
                        <input
                            type="text"
                            name="cuisine"
                            className="form-control"
                            placeholder="Enter Cuisine"
                            value={values.Cuisine}
                            onChange={e => setValues({ ...values, Cuisine: e.target.value })}
                            required
                        />
                        {errors.Cuisine && <div className="text-danger">{errors.Cuisine}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="totalTables">Total Tables:</label>
                        <input
                            type="number"
                            name="totalTables"
                            className="form-control"
                            placeholder="Enter Total Tables"
                            value={values.TotalTables}
                            onChange={e => setValues({ ...values, TotalTables: parseInt(e.target.value) || 0 })}
                            required
                        />
                        {errors.TotalTables && <div className="text-danger">{errors.TotalTables}</div>}
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="email">Personal Email:</label>
                        <input
                            type="email"
                            name="email"
                            className="form-control"
                            placeholder="Enter Email"
                            value={values.Personal_Email}
                            onChange={e => setValues({ ...values, Personal_Email: e.target.value })}
                            required
                        />
                        {errors.Personal_Email && <div className="text-danger">{errors.Personal_Email}</div>}
                    </div><br/>
                    <button className="btn btn-success m-1" type="submit" >Save</button>
                    <Link to="/adminview" className="btn btn-primary m-1">Back</Link>
                </form>
            </div>
        </div>
    </>
   
  );
};

export default UpdateRestaurantForm;
