import React, { useState, useEffect } from 'react';
import axios from 'axios';

function DisplayService() {
    const [menu, setMenu] = useState({
        name: '',
        price: '',
        menuImage: null,
        uniqueFileName: ''
    });
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMenu({ ...menu, [name]: value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setMenu({ ...menu, menuImage: file });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!menu.name || !menu.price || !menu.menuImage || !menu.uniqueFileName) {
            setError('All fields are required');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('name', menu.name);
            formData.append('price', menu.price);
            formData.append('menuImage', menu.menuImage);
            formData.append('uniqueFileName', menu.uniqueFileName);

            await axios.post('http://localhost:5151/api/Backend/SaveMenu', formData);

            setMenu({
                name: '',
                price: '',
                menuImage: null,
                uniqueFileName: ''
            });
            setError(null);
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" value={menu.name} onChange={handleChange} placeholder="Name" required />
                <input type="text" name="price" value={menu.price} onChange={handleChange} placeholder="Price" required />
                <input type="file" name="menuImage" onChange={handleFileChange} required />
                <input type="text" name="uniqueFileName" value={menu.uniqueFileName} onChange={handleChange} placeholder="Unique File Name" required />
                <button type="submit">Submit</button>
            </form>
            {error && <p>{error}</p>}
        </div>
    );
}

export default DisplayService;
