import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './index.css'

function Home() {
    const [data, setData] = useState([])
    const navigate = useNavigate();
    useEffect(() => {
        axios.get('http://localhost:3001/users ')
            .then(res => setData(res.data))

            .catch(err => console.log(err));
    }, [])

    const handleDelete = (id) => {
        const confirm = window.confirm("Would you like to delete?");
        if (confirm) {
            axios.delete('http://localhost:3001/users/' + id)
                .then(res => {
                    window.location.reload();
                }).catch(err => console.log(err));
        }
    }


    return (
        <>
            <h1>List of Medicine</h1>
            <div className="d-flex flex-column  align-items-center backgroundcolorset vh-100">
                
                <div className="w-100 rounded bg-info border shadow p-4">
                    <div className="d-flex justify-content-end ">
                        <Link to="/create" className="btn btn-sm btn-success">Add +</Link>
                    </div>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>MEDICINE_ID</th>
                                <th>MEDICINE_NAME</th>
                                <th>MEDICINE_EXPIRY_DATE</th>
                                <th>MEDICINE_CATEGORY</th>
                                <th>MEDICINE_RATE</th>
                                <th>MEDICINE_STOCK</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                data.map((d, i) => (
                                    <tr key={i}>
                                        <td>{d.id}</td>
                                        <td>{d.medicine_Name}</td>
                                        <td>{d.medicine_Expiry_Date}</td>
                                        <td>{d.medicine_Category}</td>
                                        <td>{d.medicine_Rate}</td>
                                        <td>{d.medicine_Stock}</td>
                                        <td>
                                            <Link to={`/read/${d.id}`} className="btn btn-info m-1">View Details</Link>
                                            <Link to={`/update/${d.id}`} className="btn btn-primary m-1">Edit Details</Link>
                                            <button onClick={e => handleDelete(d.id)} className="btn btn-danger m-1">Delete Details</button>
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );

}
export default Home;
