import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Cookies from 'js-cookie';
import Layout from "../components/Layout/Layout";
import { MarkEmailRead } from "@mui/icons-material";

function BookingView() {
    // const [restaurants, getRestaurants] = useState([]);
    const [customerID, getCustomerID] = useState('');
    
    const email = Cookies.get('Email');
    console.log(email);
    const [values, getValues] = useState({
        customer_Id: '',
        date: '',
        time: '',
        restaurant_Id: 1, // Assuming you have a way to get the restaurant ID
        noOfTables: ''
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`https://localhost:7092/api/Reservation`,{
                    customer_Email:email
                    
                })
                console.log(restaurants)
                getCustomerID(response.data.customer_Id);
                // Save to cookies
                Cookies.set('restaurants', JSON.stringify(response.data));
            } catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, [email]);
    useEffect(() => {
        getValues({ ...values, customer_Id: customerID });
    }, [customerID]);


    const handleDelete = async (id) => {
        const confirmDelete = window.confirm("Would you like to delete?");
        if (confirmDelete) {
            try {
                await axios.delete(`https://localhost:7092/api/Reservation/${id}`);
                const updatedRestaurants = restaurants.filter(restaurant => restaurant.reqId !== id);
                getRestaurants(updatedRestaurants);
                // Update cookies
                Cookies.set('restaurants', JSON.stringify(updatedRestaurants));
            } catch (err) {
                console.log(err);
            }
        }
    };

    return (
        <Layout>
            <h1>Booking Cart</h1> <Link to="/home" className="btn btn-primary m-1">Back</Link>
            <div className="cart-container">
                {/* {restaurants.map((restaurant, index) => ( */}
                    {/* <div key={index} className="cart-item"> */}
                        <h3>Reservation ID: {values.reqId}</h3>
                        <p>Customer Id: {values.customer_Id}</p>
                        <p>Date: {values.date}</p>
                        <p>Time: {values.time}</p>
                        <p>No Of Tables: {values.noOfTables}</p>
                        <button onClick={() => handleDelete(values.reqId)} className="btn btn-danger m-1">Remove from Cart</button>
                    {/* </div> */}
                {/* ))} */}
            </div>
        </Layout>
    );
}

export default BookingView;
