import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import Layout from "../components/Layout/Layout";
import '../styles/Login.css';

function ReservationModel() {
    const [values, setValues] = useState({
        Name:'',
        date: '',
        time: '',
        numberOfTables: ''
    });
    const [error, setErrors] = useState({});
    const navigate = useNavigate();

    const handleChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    };

      const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const response = await axios.post('https://localhost:7092/api/Reservation', values);
          console.log(response.data);
          window.alert('Table is Booked Successfully'); 
          navigate('/home');
        } catch (error) {
          setErrors(error.response.data.message);
        }
      };
    
     


    const validateForm = () => {
        let errors = {};
        let isValid = true;
        if (!values.date) {
            errors.date = "Name is required";
            isValid = false;
        }


        if (!values.date) {
            errors.date = "Date is required";
            isValid = false;
        }

        if (!values.time) {
            errors.time = "Time is required";
            isValid = false;
        }

        if (!values.numberOfTables) {
            errors.numberOfTables = "Number of tables is required";
            isValid = false;
        }

        setErrors(errors);
        return isValid;
    };

    return (
        <Layout>
          
            <div className="d-flex w-100 vh-100 justify-content-center backgroundcolorset">
                <div className="w-50 border border-black shadow  px-5 pt-3 pb-5 rounded border-set">
                    <h3 className="head">Make a Reservation</h3><br />
                    <form onSubmit={handleSubmit}>
                    <div className="mb-2">
                        <label htmlFor="name"> Name:</label>
                        <input
                            type="text"
                            name="name"
                            className="form-control"
                            placeholder="Enter Customer Name"
                            value={values.name}
                            onChange={e => setValues({ ...values, Name: e.target.value })}
                            required
                        />
                        {errors.Name && <div className="text-danger">{errors.Name}</div>}
                    </div><br/>
                   
                        <div className="mb-2">
                            <label className="book-color"  htmlFor="date">Date:</label>
                            <input
                                required
                                type="date"
                                name="date"
                                className="form-control"
                                placeholder="Enter Date"
                                onChange={handleChange}
                            />
                            {errors.date && <span className="text-danger">{errors.date}</span>}
                        </div><br />
                        <div className="mb-2">
                            <label className="book-color" htmlFor="time">Time:</label>
                            <input
                                required
                                type="time"
                                name="time"
                                className="form-control"
                                placeholder="Enter Time"
                                onChange={handleChange}
                            />
                            {errors.time && <span className="text-danger">{errors.time}</span>}
                        </div><br />
                        <div className="mb-2">
                            <label className="book-color"  htmlFor="numberOfTables">Number of Tables:</label>
                            <input
                                required
                                type="number"
                                name="numberOfTables"
                                className="form-control"
                                placeholder="Enter Number of Tables"
                                onChange={handleChange}
                            />
                            {errors.numberOfTables && <span className="text-danger">{errors.numberOfTables}</span>}
                        </div><br />
                        <button className="btn btn-success m-1" type="submit">Save</button>
                        <Link to="/" className="btn btn-primary m-1">Back</Link>
                    </form>
                    {error && <div className="alert alert-danger" role="alert">{error}</div>}
                </div>
            </div>
        </Layout>
    );
}

export default ReservationModel;
