import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Card() {
    const [restaurants, setRestaurants] = useState([]);
    console.log(restaurants);
    useEffect(() => {
        axios.get('https://localhost:7092/api/Menu')
            .then(res => setRestaurants(res.data))
            .catch(err => console.log(err));
         
    }, []);

    // const handleDelete = (id) => {
    //     const confirmDelete = window.confirm("Would you like to delete?");
    //     if (confirmDelete) {
    //         axios.delete(`https://localhost:7092/api/Restaurant_Details/${id}`)
    //             .then(res => {
    //                 setRestaurants(restaurants.filter(restaurant => restaurant.restaurant_Id !== id));
    //             })
    //             .catch(err => console.log(err));
    //     }
    // };

    return (
        <>
            <h1>List of Menus</h1>
            <div className="d-flex flex-column  align-items-center backgroundcolorset vh-100">
                <div className="w-100 rounded bg-dark border shadow p-4">
                    <div className="d-flex justify-content-end">
                        <Link to="/restaurant" className="btn btn-sm btn-success">Add +</Link>
                    </div>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>Menu ID</th>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Menu Image</th>
                                
                            </tr>
                        </thead>
                        <tbody>
                            {
                                restaurants.map((restaurant, index) => (
                                    <tr key={index}>
                                        <td>{restaurant.menuID}</td>
                                        <td>{restaurant.name}</td>
                                        <td>{restaurant.price}</td>
                                        <td>{restaurant.menuImage}</td>
                                       
                                        {/* <td> */}
                                            {/* <Link to={`/adminread`} className="btn btn-info m-1">View Details</Link> */}
                                            {/* <Link to={`/adminupdate/${restaurant.restaurant_Id}`} className="btn btn-primary m-1">Edit Details</Link> */}
                                            {/* <button onClick={() => handleDelete(restaurant.restaurant_Id)} className="btn btn-danger m-1">Delete</button> */}
                                        {/* </td> */}
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                    <div className="menus-container panel panel-default text-center">
                {restaurants.map((menu) => (
                    <div key={menu.MenuID} className="menu-item">
                        <div className="panel-header">
                            <h2>{menu.Name}</h2>
                        </div>
                        <div className="panel-body">
                            <img src={restaurant.MenuImage} alt={menu.Name} />
                        </div>
                        <div className="panel-footer">
                          
                            <p>Price: {menu.Price}</p>
                            <button className="btn btn-lg" type="button">View Details</button>
                        </div>
                    </div>
                ))}
            </div> 
                  
                </div>
            </div>
        </>
    );
}

export default Card;
