import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Card() {
    const [menus, setMenu] = useState([]);
    useEffect(() => {
        axios.get('https://localhost:7092/api/Menu')
            .then(res => setMenu(res.data))
            .catch(err => console.log(err));
    }, []);

    // const handleDelete = (id) => {
    //     const confirmDelete = window.confirm("Would you like to delete?");
    //     if (confirmDelete) {
    //         axios.delete(`https://localhost:7092/api/Restaurant_Details/${id}`)
    //             .then(res => {
    //                 setMenu(menus.filter(menu => menu.menu_Id !== id));
    //             })
    //             .catch(err => console.log(err));
    //     }
    // };

    return (
        <>
            <h1>List of Restaurants</h1>
            <div className="d-flex flex-column align-items-center backgroundcolorset vh-100">
                <div className="w-100 rounded bg-dark border shadow p-4">
                    <div className="d-flex justify-content-end">
                        <Link to="/menu" className="btn btn-sm btn-success">Add +</Link>
                    </div>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>Menu ID</th>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Menu Image</th>
                              
                            </tr>
                        </thead>
                        <tbody>
                            {
                                menus.map((menu, index) => (
                                    <tr key={index}>
                                        <td>{menu.menu_Id}</td>
                                        <td>{menu.name}</td>
                                        <td>{menu.price}</td>
                                        <td>{menu.menu_Image}</td>
                                       
                                        {/* <td>
                                            <Link to={`/adminread/${restaurant.restaurant_Id}`} className="btn btn-info m-1">View Details</Link>
                                            <Link to={`/adminupdate/${restaurant.restaurant_Id}`} className="btn btn-primary m-1">Edit Details</Link>
                                            <button onClick={() => handleDelete(restaurant.restaurant_Id)} className="btn btn-danger m-1">Delete</button>
                                </td>*/}
                                    </tr> 
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Card;
