import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
function AdminCreate() {
    const [values, setValues] = useState({
        medicine_Name: '',
        medicine_Expiry_Date: '',
        medicine_Category: '',
        medicine_Rate:'',
        medicine_Stock:''
    })
    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('http://localhost:3001/users ', values)
            .then(res => {
                console.log(res);
                navigate('/Admin')
            })
            .catch(err => console.log(err));
    }
    return (
        <>
         <h1>PHARMACY MANAGEMENT SYSTEM</h1>
        <div className="d-flex w-100 vh-100 justify-content-center backgroundcolorset">
            <div className="w-50 border  border-black shadow bg-info px-5 pt-3 pb-5 rounded ">
                <h3>Add Medicine</h3><br/>
                <form onSubmit={handleSubmit}>
                    <div className="mb-2">
                        <label htmlFor="name">Medicine Name:</label>
                        <input required type="text" name="name" className="form-control" placeholder="Enter Medicine Name"
                            onChange={e => setValues({ ...values,  medicine_Name: e.target.value })} />
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="date">Medicine Expiry Date:</label>
                        <input required type="date" name="date" className="form-control" placeholder="Enter Expiry date"
                            onChange={e => setValues({ ...values, medicine_Expiry_Date: e.target.value })} />
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="name">Medicine Category:</label>
                        <input required type="text" name="name" className="form-control" placeholder="Enter Medicine Category"
                            onChange={e => setValues({ ...values,  medicine_Category: e.target.value })} />
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="number">Medicine rate:</label>
                        <input required type="text" name="number" className="form-control" placeholder="Enter Medicine Rate"
                            onChange={e => setValues({ ...values, medicine_Rate: e.target.value })} />
                    </div><br/>
                    <div className="mb-2">
                        <label htmlFor="number">Medicine Stock:</label>
                        <input required type="text" name="number" className="form-control" placeholder="Enter Medicne stock"
                            onChange={e => setValues({ ...values, medicine_Stock: e.target.value })} />
                    </div><br/>
                    <button className="btn btn-success m-1" type="submit" >Save</button>
                    <Link to="/" className="btn btn-primary m-1">Back</Link>
                </form>
            </div>
        </div>
        </>
    );
}
export default AdminCreate;
