const RestaurantDetails = () => {
  return (
    <Layout>
      <div sx={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
        {MenuList.map((menu) => (
          <div  sx={{ maxWidth: "390px", display: "flex", m: 2 }}>
            <div>
              <div
                sx={{ minHeight: "400px" }}
                component={"img"}
                src={menu.image}
                alt={menu.name}
              />
              <div>
                <div variant="h5" gutterBottom component={"div"}>
                  {menu.name}
                </div>
                <div variant="body2">{menu.description}</div>
              </div>
              
          
            </div>
          
          </div>
          
        ))}
      </div>
    </Layout>
  );
};

