import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const UpdateRestaurantForm = () => {
  const { sid } = useParams();
  const navigate = useNavigate();

  const [restaurant, setRestaurant] = useState({
    Restaurant_Name: '',
    Email_Id: '',
    ContactNumber: '',
    Password: '',
    Location: '',
    Type: '',
    Cuisine: '',
    TotalTables: 0,
    Status: 'waiting for approval',
    Personal_Email: '',
    RestaurantImage: null,
    UniqueFileName: '',
  });

  useEffect(() => {
    axios.get(`http://localhost:5151/api/Backend/GetById/${sid}`)
      .then((res) => {
        const restaurantData = res.data;
        setRestaurant(restaurantData);
        console.log('Get data:', restaurantData);
      })
      .catch((error) => {
        if (error.response && error.response.status === 404) {
          console.log('Restaurant not found');
        }
        console.log('Error:', error.message);
      });
  }, [sid]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRestaurant((prevRestaurant) => ({
      ...prevRestaurant,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    setRestaurant({
      ...restaurant,
      RestaurantImage: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('Restaurant_Name', restaurant.Restaurant_Name);
    formData.append('Email_Id', restaurant.Email_Id);
    formData.append('ContactNumber', restaurant.ContactNumber);
    formData.append('Password', restaurant.Password);
    formData.append('Location', restaurant.Location);
    formData.append('Type', restaurant.Type);
    formData.append('Cuisine', restaurant.Cuisine);
    formData.append('TotalTables', restaurant.TotalTables);
    formData.append('Status', restaurant.Status);
    formData.append('Personal_Email', restaurant.Personal_Email);
    formData.append('RestaurantImage', restaurant.RestaurantImage);

    try {
      await axios.put(`http://localhost:5151/api/Backend/UpdateRestaurant/${sid}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('Restaurant updated successfully');
      console.log('Restaurant updated successfully');
      // Redirect to the updated restaurant details page
      navigate(`/restaurant/${sid}`);
    } catch (error) {
      console.error('Error updating restaurant:', error);
    }
  };

  return (
    <></>
    // Your JSX for the form goes here
    // ...
  );
};

export default UpdateRestaurantForm;
