import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Cookies from 'js-cookie';
import Layout from "../components/Layout/Layout";

function BookingView() {
    const [customerID, setCustomerID] = useState('');
    const [bookings, setBookings] = useState([]);
    const email = Cookies.get('Email');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`https://localhost:7092/api/Reservation`, {
                    params: {
                        customer_Email: email
                    }
                });
                setCustomerID(response.data.customer_Id);
                // Save customer ID to cookies
                Cookies.set('CustomerID', response.data.customer_Id);

                const allBookingsResponse = await axios.get(`https://localhost:7092/api/Reservation`);
                setBookings(allBookingsResponse.data);
            } catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, [email]);

    const handleDelete = async (id) => {
        const confirmDelete = window.confirm("Would you like to delete?");
        if (confirmDelete) {
            try {
                await axios.delete(`https://localhost:7092/api/Reservation/${id}`);
                const updatedBookings = bookings.filter(booking => booking.reqId !== id);
                setBookings(updatedBookings);
            } catch (err) {
                console.log(err);
            }
        }
    };

    const filteredBookings = bookings.filter(booking => booking.customer_Id === customerID);
    console.log(filteredBookings);

    return (
        <Layout>
            <h1>Booking Cart</h1>
            <Link to="/home" className="btn btn-primary m-1">Back</Link>
            <div className="cart-container">
                {filteredBookings.length>0?(filteredBookings.map((booking, index) => (
                    <div key={index} className="cart-item">
                        <h3>Reservation ID: {booking.reqId}</h3>
                        <p>Customer Id: {booking.customer_Id}</p>
                        <p>Date: {booking.date}</p>
                        <p>Time: {booking.time}</p>
                        <p>No Of Tables: {booking.noOfTables}</p>
                        <button onClick={() => handleDelete(booking.reqId)} className="btn btn-danger m-1">Remove from Cart</button>
                    </div>
                ))
            ): (<p>no bookings fou</p>)}
            </div>
        </Layout>
    );
}

export default BookingView;
