import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Cookies from 'js-cookie';
import Layout from "../components/Layout/Layout";

function BookingView() {
    // const [restaurants, setRestaurants] = useState([]);

    // useEffect(() => {
    //     const fetchData = async () => {
    //         try {
    //             const response = await axios.get(`https://localhost:7092/api/Reservation`);
    //             setRestaurants(response.data);
    //             // Save to cookies
    //             Cookies.set('restaurants', JSON.stringify(response.data));
    //         } catch (err) {
    //             console.log(err);
    //         }
    //     };
    //     fetchData();
    // }, []);


    const [customerID, setCustomerID] = useState('');
    const email = Cookies.get('Email');
    // const [values, setValues] = useState({
    //     customer_Id: '',
    //     date: '',
    //     time: '',
    //     restaurant_Id: 1, // Assuming you have a way to get the restaurant ID
    //     noOfTables: ''
    // });
    const [maxtable, setMaxTable] = useState(
        200);
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        
        axios.get('https://localhost:7092/api/Reservation', {
            customer_Email: email
        })
            .then(res => {
                console.log("fetch", res.data.customer_Id);
                setCustomerID(res.data.customer_Id);
            })
            .catch(err => console.log(err));
    }, [email]);

    useEffect(() => {
        setValues({ ...values, customer_Id: customerID });
    }, [customerID]);

    // const handleChange = (e) => {
    //     console.log(values)
    //     setValues({ ...values, [e.target.name]: e.target.value });
       
        


    // };

    const handleDelete = async (id) => {
        const confirmDelete = window.confirm("Would you like to delete?");
        if (confirmDelete) {
            try {
                await axios.delete(`https://localhost:7092/api/Reservation/${id}`);
                const updatedRestaurants = restaurants.filter(restaurant => restaurant.reqId !== id);
                setRestaurants(updatedRestaurants);
                // Update cookies
                Cookies.set('restaurants', JSON.stringify(updatedRestaurants));
            } catch (err) {
                console.log(err);
            }
        }
    };

    return (
        <Layout>
            <h1>Booking Cart</h1> <Link to="/home" className="btn btn-primary m-1">Back</Link>
            <div className="cart-container">
                {restaurants.map((restaurant, index) => (
                    <div key={index} className="cart-item">
                        <h3>Reservation ID: {restaurant.reqId}</h3>
                        <p>Customer Id: {restaurant.customer_Id}</p>
                        <p>Date: {restaurant.date}</p>
                        <p>Time: {restaurant.time}</p>
                        <p>No Of Tables: {restaurant.noOfTables}</p>
                        <button onClick={() => handleDelete(restaurant.reqId)} className="btn btn-danger m-1">Remove from Cart</button>
                    </div>
                ))}
            </div>
        </Layout>
    );
}

export default BookingView;
